          border: 1px solid rgba(255, 255, 255, 0.2);
          animation: cardSlideIn 0.6s ease-out;
        }

        .auth-header {
          text-align: center;
          margin-bottom: 40px;
        }

        .auth-icon {
          width: 80px;
          height: 80px;
          background: linear-gradient(135deg, #667eea, #764ba2);
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
          margin: 0 auto 20px;
          font-size: 2rem;
          color: white;
        }

        .auth-title {
          font-size: 2.2rem;
          font-weight: 700;
          color: #2d3748;
          margin-bottom: 10px;
        }

        .auth-subtitle {
          color: #718096;
          font-size: 1rem;
        }

        .auth-form {
          space-y: 25px;
        }

        .form-group {
          margin-bottom: 25px;
        }

        .form-label {
          display: block;
          margin-bottom: 8px;
          font-weight: 600;
          color: #4a5568;
          font-size: 0.9rem;
        }

        .form-input {
          width: 100%;
          padding: 18px 20px;
          border: 2px solid #e2e8f0;
          border-radius: 15px;
          font-size: 1rem;
          transition: all 0.3s ease;
          background: #f8fafc;
        }

        .form-input:focus {
          outline: none;
          border-color: #667eea;
          background: white;
          box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
          transform: translateY(-2px);
        }

        .form-input::placeholder {
          color: #a0aec0;
        }

        .error-message {
          background: #fed7d7;
          color: #c53030;
          padding: 15px 20px;
          border-radius: 12px;
          margin-bottom: 25px;
          border-left: 4px solid #f56565;
          animation: shake 0.5s ease-in-out;
        }

        .submit-button {
          width: 100%;
