import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import API from '../utils/api';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [myGroups, setMyGroups] = useState([]);
  const [pendingInvites, setPendingInvites] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [userResponse, groupsResponse, invitesResponse] = await Promise.all([
        API.get('/auth/profile'),
        API.get('/groups/my-groups'),
        API.get('/groups/pending-invites')
      ]);

      setUser(userResponse.data);
      setMyGroups(groupsResponse.data);
      setPendingInvites(invitesResponse.data);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInviteResponse = async (groupId, action) => {
    try {
      await API.put(`/groups/${groupId}/respond`, { action });
      fetchDashboardData(); // Refresh data
    } catch (error) {
      console.error('Error responding to invite:', error);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-lg">Loading...</div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        {/* User Profile */}
        <div className="bg-white overflow-hidden shadow rounded-lg mb-6">
          <div className="px-4 py-5 sm:px-6">
            <h3 className="text-lg leading-6 font-medium text-gray-900">Profile Information</h3>
          </div>
          <div className="border-t border-gray-200 px-4 py-5 sm:p-6">
            <dl className="grid grid-cols-1 gap-x-4 gap-y-6 sm:grid-cols-2">
              <div>
                <dt className="text-sm font-medium text-gray-500">Full Name</dt>
                <dd className="mt-1 text-sm text-gray-900">{user?.name}</dd>
              </div>
              <div>
                <dt className="text-sm font-medium text-gray-500">Email address</dt>
                <dd className="mt-1 text-sm text-gray-900">{user?.email}</dd>
              </div>
              <div>
                <dt className="text-sm font-medium text-gray-500">Mobile Number</dt>
                <dd className="mt-1 text-sm text-gray-900">{user?.mobile}</dd>
              </div>
            </dl>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* My Groups */}
          <div className="bg-white overflow-hidden shadow rounded-lg">
            <div className="px-4 py-5 sm:px-6 flex justify-between items-center">
              <h3 className="text-lg leading-6 font-medium text-gray-900">My Groups</h3>
              <Link 
                to="/groups" 
                className="bg-primary-600 text-white px-4 py-2 rounded-md hover:bg-primary-700 text-sm"
              >
                Manage Groups
              </Link>
            </div>
            <div className="border-t border-gray-200">
              {myGroups.length === 0 ? (
                <div className="px-4 py-5 sm:p-6 text-center text-gray-500">
                  You haven't joined any groups yet.
                </div>
              ) : (
                <ul className="divide-y divide-gray-200">
                  {myGroups.map((group) => (
                    <li key={group._id} className="px-4 py-4">
                      <div className="flex items-center justify-between">
                        <div>
                          <h4 className="text-sm font-medium text-gray-900">{group.name}</h4>
                          <p className="text-sm text-gray-500">
                            {group.members.filter(m => m.status === 'accepted').length} members
                          </p>
                        </div>
                        <Link
                          to={`/reports/${group._id}`}
                          className="text-primary-600 hover:text-primary-500 text-sm"
                        >
                          View Report
                        </Link>
                      </div>
                    </li>
                  ))}
                </ul>
              )}
            </div>
          </div>

          {/* Pending Invites */}
          <div className="bg-white overflow-hidden shadow rounded-lg">
            <div className="px-4 py-5 sm:px-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900">Pending Invites</h3>
            </div>
            <div className="border-t border-gray-200">
              {pendingInvites.length === 0 ? (
                <div className="px-4 py-5 sm:p-6 text-center text-gray-500">
                  No pending invites.
                </div>
              ) : (
                <ul className="divide-y divide-gray-200">
                  {pendingInvites.map((group) => (
                    <li key={group._id} className="px-4 py-4">
                      <div className="flex items-center justify-between">
                        <div>
                          <h4 className="text-sm font-medium text-gray-900">{group.name}</h4>
                          <p className="text-sm text-gray-500">
                            Created by {group.createdBy.name}
                          </p>
                        </div>
                        <div className="flex space-x-2">
                          <button
                            onClick={() => handleInviteResponse(group._id, 'accept')}
                            className="bg-green-600 text-white px-3 py-1 rounded text-sm hover:bg-green-700"
                          >
                            Accept
                          </button>
                          <button
                            onClick={() => handleInviteResponse(group._id, 'reject')}
                            className="bg-red-600 text-white px-3 py-1 rounded text-sm hover:bg-red-700"
                          >
                            Reject
                          </button>
                        </div>
                      </div>
                    </li>
                  ))}
                </ul>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
