import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navbar = ({ user, onLogout }) => {
  const location = useLocation();

  return (
    <nav className="navbar">
      <style jsx>{`
        .navbar {
          background: rgba(255, 255, 255, 0.95);
          backdrop-filter: blur(20px);
          border-bottom: 1px solid rgba(255, 255, 255, 0.2);
          box-shadow: 0 4px 30px rgba(0, 0, 0, 0.1);
          padding: 0 20px;
          position: sticky;
          top: 0;
          z-index: 1000;
        }

        .navbar-content {
          max-width: 1200px;
          margin: 0 auto;
          display: flex;
          justify-content: space-between;
          align-items: center;
          height: 70px;
        }

        .navbar-brand {
          display: flex;
          align-items: center;
          text-decoration: none;
        }

        .brand-icon {
          width: 40px;
          height: 40px;
          background: linear-gradient(135deg, #667eea, #764ba2);
          border-radius: 12px;
          display: flex;
          align-items: center;
          justify-content: center;
          margin-right: 12px;
          font-size: 1.2rem;
          color: white;
        }

        .brand-text {
          font-size: 1.5rem;
          font-weight: 700;
          color: #2d3748;
        }

        .navbar-nav {
          display: flex;
          align-items: center;
          gap: 10px;
        }

        .nav-link {
          padding: 12px 20px;
          text-decoration: none;
          color: #4a5568;
          font-weight: 600;
          border-radius: 12px;
          transition: all 0.3s ease;
          position: relative;
        }

        .nav-link:hover {
          color: #667eea;
          background: rgba(102, 126, 234, 0.1);
        }

        .nav-link.active {
          color: #667eea;
          background: rgba(102, 126, 234, 0.1);
        }

        .nav-link.active::after {
          content: '';
          position: absolute;
          bottom: -1px;
          left: 20px;
          right: 20px;
          height: 3px;
          background: linear-gradient(135deg, #667eea, #764ba2);
          border-radius: 3px;
        }

        .navbar-user {
          display: flex;
          align-items: center;
          gap: 20px;
        }

        .user-info {
          display: flex;
          align-items: center;
          gap: 12px;
        }

        .user-avatar {
          width: 40px;
          height: 40px;
          background: linear-gradient(135deg, #667eea, #764ba2);
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
          color: white;
          font-weight: 600;
          font-size: 0.9rem;
        }

        .user-name {
          color: #2d3748;
          font-weight: 600;
        }

        .logout-button {
          background: linear-gradient(135deg, #f56565, #e53e3e);
          color: white;
          padding: 10px 20px;
          border: none;
          border-radius: 12px;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s ease;
        }

        .logout-button:hover {
          transform: translateY(-2px);
          box-shadow: 0 8px 20px rgba(245, 101, 101, 0.3);
        }

        @media (max-width: 768px) {
          .navbar-content {
            flex-direction: column;
            height: auto;
            padding: 15px 0;
          }

          .navbar-nav {
            margin: 15px 0;
          }

          .user-name {
            display: none;
          }
        }
      `}</style>

      <div className="navbar-content">
        <Link to="/dashboard" className="navbar-brand">
          <div className="brand-icon">ðŸ’°</div>
          <span className="brand-text">ExpenseSplit</span>
        </Link>

        <div className="navbar-nav">
          <Link 
            to="/dashboard" 
            className={`nav-link ${location.pathname === '/dashboard' ? 'active' : ''}`}
          >
            Dashboard
          </Link>
          <Link 
            to="/groups" 
            className={`nav-link ${location.pathname === '/groups' ? 'active' : ''}`}
          >
            My Groups
          </Link>
        </div>

        <div className="navbar-user">
          <div className="user-info">
            <div className="user-avatar">
              {user.name.charAt(0).toUpperCase()}
            </div>
            <span className="user-name">Welcome, {user.name}</span>
          </div>
          <button
            onClick={onLogout}
            className="logout-button"
          >
            Logout
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
