import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from '../utils/api';

const ReportsPage = ({ user }) => {
  const { groupId } = useParams();
  const [report, setReport] = useState(null);
  const [expenses, setExpenses] = useState([]);
  const [showAddExpense, setShowAddExpense] = useState(false);
  const [newExpense, setNewExpense] = useState({
    description: '',
    amount: '',
    splitType: 'equal'
  });
  const [loading, setLoading] = useState(true);
  const [addExpenseLoading, setAddExpenseLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchReportData();
  }, [groupId]);

  const fetchReportData = async () => {
    try {
      setError('');
      const [reportResponse, expensesResponse] = await Promise.all([
        api.get(`/expenses/report/${groupId}`),
        api.get(`/expenses/group/${groupId}`)
      ]);
      
      setReport(reportResponse.data);
      setExpenses(expensesResponse.data);
    } catch (error) {
      console.error('Error fetching report data:', error);
      setError('Failed to load report data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleAddExpense = async (e) => {
    e.preventDefault();
    
    // Validation
    if (!newExpense.description.trim()) {
      setError('Please enter a description');
      return;
    }
    
    if (!newExpense.amount || parseFloat(newExpense.amount) <= 0) {
      setError('Please enter a valid amount');
      return;
    }

    setAddExpenseLoading(true);
    setError('');

    try {
      console.log('Adding expense:', {
        ...newExpense,
        groupId,
        amount: parseFloat(newExpense.amount)
      });

      const response = await api.post('/expenses', {
        description: newExpense.description.trim(),
        amount: parseFloat(newExpense.amount),
        groupId: groupId,
        splitType: newExpense.splitType
      });
      
      console.log('Expense added successfully:', response.data);
      
      // Reset form and close modal
      setNewExpense({ description: '', amount: '', splitType: 'equal' });
      setShowAddExpense(false);
      
      // Refresh data
      await fetchReportData();
      
    } catch (error) {
      console.error('Error adding expense:', error);
      const errorMessage = error.response?.data?.message || 
                          error.response?.data?.error || 
                          'Failed to add expense. Please try again.';
      setError(errorMessage);
    } finally {
      setAddExpenseLoading(false);
    }
  };

  const handleMarkAsPaid = async (expenseId, userId) => {
    try {
      await api.post('/expenses/mark-paid', { expenseId, userId });
      fetchReportData(); // Refresh data
    } catch (error) {
      console.error('Error marking as paid:', error);
      setError('Failed to mark as paid. Please try again.');
    }
  };

  const resetForm = () => {
    setNewExpense({ description: '', amount: '', splitType: 'equal' });
    setError('');
    setShowAddExpense(false);
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="text-center">Loading report...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        
        {/* Error Message */}
        {error && (
          <div className="mb-6 bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
            <strong>Error:</strong> {error}
          </div>
        )}

        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold text-gray-900">Expense Report</h1>
          <button
            onClick={() => setShowAddExpense(true)}
            className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md text-sm font-medium"
          >
            Add Expense
          </button>
        </div>

        {/* Settlement Summary */}
        <div className="bg-white shadow rounded-lg mb-6">
          <div className="px-4 py-5 sm:px-6">
            <h3 className="text-lg leading-6 font-medium text-gray-900">Settlement Summary</h3>
          </div>
          <div className="border-t border-gray-200 px-4 py-5 sm:p-6">
            {report && report.settlements && report.settlements.length > 0 ? (
              <div className="space-y-3">
                {report.settlements.map((settlement, index) => (
                  <div key={index} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <div className="flex items-center space-x-3">
                      <span className="text-sm font-medium text-gray-900">
                        {settlement.from?.name}
                      </span>
                      <span className="text-gray-500">‚Üí</span>
                      <span className="text-sm font-medium text-gray-900">
                        {settlement.to?.name}
                      </span>
                    </div>
                    <div className="flex items-center space-x-3">
                      <span className="text-lg font-bold text-green-600">
                        ‚Çπ{settlement.amount}
                      </span>
                      <button
                        onClick={() => {
                          alert(`Payment of ‚Çπ${settlement.amount} from ${settlement.from?.name} to ${settlement.to?.name} simulated successfully!`);
                        }}
                        className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded text-sm"
                      >
                        Pay Now
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-gray-500 text-center">All expenses are settled! üéâ</p>
            )}
          </div>
        </div>

        {/* Expenses List */}
        <div className="bg-white shadow rounded-lg">
          <div className="px-4 py-5 sm:px-6">
            <h3 className="text-lg leading-6 font-medium text-gray-900">All Expenses</h3>
          </div>
          <div className="border-t border-gray-200">
            <ul className="divide-y divide-gray-200">
              {expenses.map((expense) => (
                <li key={expense._id} className="px-4 py-4">
                  <div className="flex items-center justify-between">
                    <div className="flex-1">
                      <h4 className="text-sm font-medium text-gray-900">{expense.description}</h4>
                      <p className="text-sm text-gray-500">
                        Paid by {expense.paidBy?.name} ‚Ä¢ ‚Çπ{expense.amount} ‚Ä¢ {expense.splitType} split
                      </p>
                      <div className="mt-2 space-y-1">
                        {expense.splits?.map((split, index) => (
                          <div key={index} className="flex items-center justify-between text-sm">
                            <span className={split.paid ? 'text-green-600' : 'text-gray-600'}>
                              {split.user?.name} owes ‚Çπ{split.amount}
                            </span>
                            <div className="flex items-center space-x-2">
                              <span className={split.paid ? 'text-green-600' : 'text-red-600'}>
                                {split.paid ? '‚úÖ Paid' : '‚ùå Not Paid'}
                              </span>
                              {!split.paid && expense.paidBy && !expense.paidBy._id.equals(split.user?._id) && (
                                <button
                                  onClick={() => handleMarkAsPaid(expense._id, split.user._id)}
                                  className="text-blue-600 hover:text-blue-800 text-sm"
                                >
                                  Mark Paid
                                </button>
                              )}
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                </li>
              ))}
              {expenses.length === 0 && (
                <li className="px-4 py-8 text-center text-gray-500">
                  No expenses yet. Add your first expense!
                </li>
              )}
            </ul>
          </div>
        </div>

        {/* Add Expense Modal */}
        {showAddExpense && (
          <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
            <div className="relative top-20 mx-auto p-5 border w-full max-w-md shadow-lg rounded-md bg-white">
              <div className="mt-3">
                <h3 className="text-lg font-medium text-gray-900 mb-4">Add New Expense</h3>
                
                <form onSubmit={handleAddExpense}>
                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Description *
                    </label>
                    <input
                      type="text"
                      required
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                      placeholder="e.g., Dinner, Hotel, Taxi"
                      value={newExpense.description}
                      onChange={(e) => setNewExpense({ ...newExpense, description: e.target.value })}
                      disabled={addExpenseLoading}
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Amount (‚Çπ) *
                    </label>
                    <input
                      type="number"
                      step="0.01"
                      min="0.01"
                      required
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                      placeholder="0.00"
                      value={newExpense.amount}
                      onChange={(e) => setNewExpense({ ...newExpense, amount: e.target.value })}
                      disabled={addExpenseLoading}
                    />
                  </div>

                  <div className="mb-6">
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Split Type
                    </label>
                    <select
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                      value={newExpense.splitType}
                      onChange={(e) => setNewExpense({ ...newExpense, splitType: e.target.value })}
                      disabled={addExpenseLoading}
                    >
                      <option value="equal">Equal Split</option>
                      <option value="custom">Custom Split</option>
                    </select>
                  </div>

                  <div className="flex justify-end space-x-3">
                    <button
                      type="button"
                      onClick={resetForm}
                      disabled={addExpenseLoading}
                      className="px-4 py-2 text-sm font-medium text-gray-700 hover:text-gray-500 disabled:opacity-50"
                    >
                      Cancel
                    </button>
                    <button
                      type="submit"
                      disabled={addExpenseLoading}
                      className="px-4 py-2 text-sm font-medium text-white bg-blue-600 rounded-md hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
                    >
                      {addExpenseLoading ? (
                        <>
                          <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                          </svg>
                          Adding...
                        </>
                      ) : (
                        'Add Expense'
                      )}
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ReportsPage;
