import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../utils/api';

const Dashboard = ({ user }) => {
  const [groups, setGroups] = useState([]);
  const [pendingInvites, setPendingInvites] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setError('');
      const [groupsResponse, invitesResponse] = await Promise.all([
        api.get('/groups'),
        api.get('/groups/invites')
      ]);
      
      setGroups(groupsResponse.data);
      setPendingInvites(invitesResponse.data);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      if (error.response?.status === 401) {
        setError('Authentication failed. Please login again.');
        localStorage.removeItem('token');
        setTimeout(() => {
          navigate('/login');
        }, 2000);
      } else {
        setError('Failed to load dashboard data. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleInviteResponse = async (groupId, action) => {
    try {
      await api.post(`/groups/${groupId}/respond`, { action });
      fetchDashboardData();
    } catch (error) {
      console.error('Error responding to invite:', error);
      if (error.response?.status === 401) {
        setError('Session expired. Please login again.');
        localStorage.removeItem('token');
        navigate('/login');
      }
    }
  };

  const compareIds = (id1, id2) => {
    if (!id1 || !id2) return false;
    return id1.toString() === id2.toString();
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="loading-spinner"></div>
        <p>Loading dashboard...</p>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-content">
        
        {error && (
          <div className="error-message">
            <strong>Error:</strong> {error}
          </div>
        )}

        <div className="profile-section">
          <div className="section-header">
            <h2>Profile Information</h2>
          </div>
          <div className="profile-grid">
            <div className="profile-item">
              <label>Full Name</label>
              <div className="profile-value">{user.name}</div>
            </div>
            <div className="profile-item">
              <label>Email</label>
              <div className="profile-value">{user.email}</div>
            </div>
            <div className="profile-item">
              <label>Mobile</label>
              <div className="profile-value">{user.mobile}</div>
            </div>
          </div>
        </div>

        <div className="dashboard-grid">
          <div className="groups-section">
            <div className="section-header">
              <h3>My Groups</h3>
              <Link to="/groups" className="primary-button">
                Manage Groups
              </Link>
            </div>
            <div className="card-list">
              {groups.slice(0, 5).map((group) => (
                <div key={group._id} className="card-item">
                  <div className="card-content">
                    <div className="card-main">
                      <h4>{group.name}</h4>
                      <div className="stats">
                        <span className="stat">
                          {group.members?.filter(m => m.status === 'accepted').length || 0} active members
                        </span>
                        {group.rejectedInvites?.length > 0 && (
                          <span className="stat rejected">
                            {group.rejectedInvites.length} rejected
                          </span>
                        )}
                        {group.members?.filter(m => m.status === 'pending').length > 0 && (
                          <span className="stat pending">
                            {group.members.filter(m => m.status === 'pending').length} pending
                          </span>
                        )}
                      </div>
                      {group.createdBy && (
                        <div className="creator-info">
                          Created by {group.createdBy.name}
                          {compareIds(group.createdBy._id, user.id) && ' (You)'}
                        </div>
                      )}
                    </div>
                    <Link
                      to={`/reports/${group._id}`}
                      className="secondary-button"
                    >
                      View Report
                    </Link>
                  </div>
                </div>
              ))}
              {groups.length === 0 && (
                <div className="empty-state">
                  No groups yet. Create your first group!
                </div>
              )}
            </div>
          </div>

          <div className="invites-section">
            <div className="section-header">
              <h3>Pending Invites</h3>
              <p>Groups you've been invited to join</p>
            </div>
            <div className="card-list">
              {pendingInvites.map((group) => (
                <div key={group._id} className="card-item">
                  <div className="card-content">
                    <div className="card-main">
                      <h4>{group.name}</h4>
                      <p className="invited-by">
                        Invited by {group.createdBy?.name}
                      </p>
                      {group.description && (
                        <p className="group-description">
                          {group.description}
                        </p>
                      )}
                    </div>
                    <div className="action-buttons">
                      <button
                        onClick={() => handleInviteResponse(group._id, 'accept')}
                        className="success-button"
                      >
                        Accept
                      </button>
                      <button
                        onClick={() => handleInviteResponse(group._id, 'reject')}
                        className="danger-button"
                      >
                        Reject
                      </button>
                    </div>
                  </div>
                </div>
              ))}
              {pendingInvites.length === 0 && (
                <div className="empty-state">
                  No pending invites
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      <style jsx>{`
        .dashboard-container {
          max-width: 1200px;
          margin: 0 auto;
          padding: 2rem 1rem;
        }

        .dashboard-loading {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          height: 50vh;
          gap: 1rem;
        }

        .loading-spinner {
          width: 40px;
          height: 40px;
          border: 3px solid #f3f4f6;
          border-top: 3px solid #3b82f6;
          border-radius: 50%;
          animation: spin 1s linear infinite;
        }

        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }

        .error-message {
          background: #fee2e2;
          border: 1px solid #fecaca;
          color: #dc2626;
          padding: 1rem;
          border-radius: 8px;
          margin-bottom: 2rem;
          animation: slideDown 0.3s ease-out;
        }

        .profile-section {
          background: white;
          border-radius: 12px;
          box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
          margin-bottom: 2rem;
          overflow: hidden;
          animation: fadeInUp 0.6s ease-out;
        }

        .section-header {
          padding: 1.5rem 2rem;
          border-bottom: 1px solid #e5e7eb;
          background: #f8fafc;
        }

        .section-header h2 {
          margin: 0;
          color: #1f2937;
          font-size: 1.5rem;
          font-weight: 600;
        }

        .section-header h3 {
          margin: 0;
          color: #1f2937;
          font-size: 1.25rem;
          font-weight: 600;
        }

        .profile-grid {
          padding: 2rem;
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
          gap: 1.5rem;
        }

        .profile-item {
          display: flex;
          flex-direction: column;
          gap: 0.5rem;
        }

        .profile-item label {
          font-size: 0.875rem;
          font-weight: 500;
          color: #6b7280;
          text-transform: uppercase;
          letter-spacing: 0.05em;
        }

        .profile-value {
          font-size: 1rem;
          color: #1f2937;
          font-weight: 500;
        }

        .dashboard-grid {
          display: grid;
          grid-template-columns: 1fr 1fr;
          gap: 2rem;
        }

        @media (max-width: 968px) {
          .dashboard-grid {
            grid-template-columns: 1fr;
          }
        }

        .groups-section, .invites-section {
          background: white;
          border-radius: 12px;
          box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
          overflow: hidden;
          animation: fadeInUp 0.6s ease-out 0.2s both;
        }

        .invites-section {
          animation-delay: 0.4s;
        }

        .card-list {
          padding: 1.5rem;
        }

        .card-item {
          border: 1px solid #e5e7eb;
          border-radius: 8px;
          margin-bottom: 1rem;
          transition: all 0.3s ease;
        }

        .card-item:hover {
          border-color: #3b82f6;
          transform: translateY(-2px);
          box-shadow: 0 8px 15px -3px rgba(0, 0, 0, 0.1);
        }

        .card-content {
          padding: 1.25rem;
          display: flex;
          justify-content: between;
          align-items: start;
          gap: 1rem;
        }

        .card-main {
          flex: 1;
        }

        .card-main h4 {
          margin: 0 0 0.5rem 0;
          color: #1f2937;
          font-size: 1rem;
          font-weight: 600;
        }

        .stats {
          display: flex;
          gap: 1rem;
          margin-bottom: 0.5rem;
          flex-wrap: wrap;
        }

        .stat {
          font-size: 0.875rem;
          color: #6b7280;
        }

        .stat.rejected {
          color: #dc2626;
        }

        .stat.pending {
          color: #d97706;
        }

        .creator-info {
          font-size: 0.75rem;
          color: #9ca3af;
          margin-top: 0.25rem;
        }

        .invited-by {
          font-size: 0.875rem;
          color: #6b7280;
          margin: 0.25rem 0;
        }

        .group-description {
          font-size: 0.75rem;
          color: #9ca3af;
          margin: 0.25rem 0 0 0;
        }

        .action-buttons {
          display: flex;
          gap: 0.5rem;
        }

        .primary-button {
          background: #3b82f6;
          color: white;
          padding: 0.75rem 1.5rem;
          border-radius: 6px;
          text-decoration: none;
          font-size: 0.875rem;
          font-weight: 500;
          transition: all 0.2s ease;
          border: none;
          cursor: pointer;
        }

        .primary-button:hover {
          background: #2563eb;
          transform: translateY(-1px);
        }

        .secondary-button {
          background: #f3f4f6;
          color: #374151;
          padding: 0.5rem 1rem;
          border-radius: 6px;
          text-decoration: none;
          font-size: 0.875rem;
          font-weight: 500;
          transition: all 0.2s ease;
          border: 1px solid #d1d5db;
        }

        .secondary-button:hover {
          background: #e5e7eb;
        }

        .success-button {
          background: #10b981;
          color: white;
          padding: 0.5rem 1rem;
          border-radius: 6px;
          border: none;
          font-size: 0.875rem;
          font-weight: 500;
          cursor: pointer;
          transition: all 0.2s ease;
        }

        .success-button:hover {
          background: #059669;
          transform: translateY(-1px);
        }

        .danger-button {
          background: #ef4444;
          color: white;
          padding: 0.5rem 1rem;
          border-radius: 6px;
          border: none;
          font-size: 0.875rem;
          font-weight: 500;
          cursor: pointer;
          transition: all 0.2s ease;
        }

        .danger-button:hover {
          background: #dc2626;
          transform: translateY(-1px);
        }

        .empty-state {
          text-align: center;
          color: #6b7280;
          padding: 2rem;
          font-style: italic;
        }

        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @keyframes slideDown {
          from {
            opacity: 0;
            transform: translateY(-10px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>
    </div>
  );
};

export default Dashboard;
