import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../utils/api';

const Dashboard = ({ user }) => {
  const [groups, setGroups] = useState([]);
  const [pendingInvites, setPendingInvites] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState('overview');
  const navigate = useNavigate();

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setError('');
      const [groupsResponse, invitesResponse] = await Promise.all([
        api.get('/groups'),
        api.get('/groups/invites')
      ]);
      
      setGroups(groupsResponse.data);
      setPendingInvites(invitesResponse.data);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      if (error.response?.status === 401) {
        setError('Authentication failed. Please login again.');
        localStorage.removeItem('token');
        setTimeout(() => {
          navigate('/login');
        }, 2000);
      } else {
        setError('Failed to load dashboard data. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleInviteResponse = async (groupId, action) => {
    try {
      await api.post(`/groups/${groupId}/respond`, { action });
      fetchDashboardData();
    } catch (error) {
      console.error('Error responding to invite:', error);
      if (error.response?.status === 401) {
        setError('Session expired. Please login again.');
        localStorage.removeItem('token');
        navigate('/login');
      }
    }
  };

  const compareIds = (id1, id2) => {
    if (!id1 || !id2) return false;
    return id1.toString() === id2.toString();
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="loading-spinner"></div>
        <p>Loading your dashboard...</p>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <style jsx>{`
        .dashboard-container {
          min-height: 100vh;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          padding: 20px;
        }

        .dashboard-content {
          max-width: 1200px;
          margin: 0 auto;
        }

        .dashboard-header {
          text-align: center;
          margin-bottom: 40px;
          animation: slideDown 0.6s ease-out;
        }

        .dashboard-header h1 {
          color: white;
          font-size: 2.5rem;
          margin-bottom: 10px;
          font-weight: 700;
        }

        .dashboard-header p {
          color: rgba(255, 255, 255, 0.8);
          font-size: 1.1rem;
        }

        .dashboard-grid {
          display: grid;
          grid-template-columns: 1fr 1fr;
          gap: 30px;
          margin-bottom: 30px;
        }

        @media (max-width: 768px) {
          .dashboard-grid {
            grid-template-columns: 1fr;
          }
        }

        .dashboard-card {
          background: white;
          border-radius: 20px;
          padding: 30px;
          box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
          backdrop-filter: blur(10px);
          border: 1px solid rgba(255, 255, 255, 0.2);
          transition: all 0.3s ease;
          animation: fadeInUp 0.6s ease-out;
        }

        .dashboard-card:hover {
          transform: translateY(-5px);
          box-shadow: 0 30px 60px rgba(0, 0, 0, 0.15);
        }

        .card-header {
          display: flex;
          justify-content: between;
          align-items: center;
          margin-bottom: 25px;
          padding-bottom: 15px;
          border-bottom: 2px solid #f0f0f0;
        }

        .card-header h3 {
          color: #2d3748;
          font-size: 1.5rem;
          font-weight: 600;
          margin: 0;
        }

        .btn-primary {
          background: linear-gradient(135deg, #667eea, #764ba2);
          color: white;
          padding: 12px 24px;
          border-radius: 12px;
          text-decoration: none;
          font-weight: 600;
          transition: all 0.3s ease;
          border: none;
          cursor: pointer;
        }

        .btn-primary:hover {
          transform: translateY(-2px);
          box-shadow: 0 10px 20px rgba(102, 126, 234, 0.3);
        }

        .group-list, .invite-list {
          list-style: none;
          padding: 0;
          margin: 0;
        }

        .group-item, .invite-item {
          padding: 20px;
          border-radius: 12px;
          margin-bottom: 15px;
          background: #f8fafc;
          border-left: 4px solid #667eea;
          transition: all 0.3s ease;
        }

        .group-item:hover, .invite-item:hover {
          background: #edf2f7;
          transform: translateX(5px);
        }

        .group-name {
          font-weight: 600;
          color: #2d3748;
          margin-bottom: 8px;
        }

        .group-meta {
          color: #718096;
          font-size: 0.9rem;
          margin-bottom: 10px;
        }

        .status-badge {
          display: inline-block;
          padding: 4px 12px;
          border-radius: 20px;
          font-size: 0.8rem;
          font-weight: 600;
          margin-right: 8px;
        }

        .status-pending {
          background: #fff3cd;
          color: #856404;
        }

        .status-rejected {
          background: #f8d7da;
          color: #721c24;
        }

        .status-accepted {
          background: #d1ecf1;
          color: #0c5460;
        }

        .btn-group {
          display: flex;
          gap: 10px;
          margin-top: 15px;
        }

        .btn-success {
          background: #48bb78;
          color: white;
          padding: 8px 16px;
          border-radius: 8px;
          border: none;
          cursor: pointer;
          font-weight: 600;
          transition: all 0.3s ease;
        }

        .btn-success:hover {
          background: #38a169;
          transform: translateY(-2px);
        }

        .btn-danger {
          background: #f56565;
          color: white;
          padding: 8px 16px;
          border-radius: 8px;
          border: none;
          cursor: pointer;
          font-weight: 600;
          transition: all 0.3s ease;
        }

        .btn-danger:hover {
          background: #e53e3e;
          transform: translateY(-2px);
        }

        .btn-secondary {
          background: #e2e8f0;
          color: #4a5568;
          padding: 8px 16px;
          border-radius: 8px;
          text-decoration: none;
          font-weight: 600;
          transition: all 0.3s ease;
          display: inline-block;
        }

        .btn-secondary:hover {
          background: #cbd5e0;
          transform: translateY(-2px);
        }

        .empty-state {
          text-align: center;
          padding: 40px 20px;
          color: #a0aec0;
        }

        .empty-state i {
          font-size: 3rem;
          margin-bottom: 15px;
          opacity: 0.5;
        }

        .error-message {
          background: #fed7d7;
          color: #c53030;
          padding: 20px;
          border-radius: 12px;
          margin-bottom: 30px;
          border-left: 4px solid #f56565;
          animation: shake 0.5s ease-in-out;
        }

        .dashboard-loading {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          min-height: 50vh;
          color: white;
        }

        .loading-spinner {
          width: 50px;
          height: 50px;
          border: 4px solid rgba(255, 255, 255, 0.3);
          border-top: 4px solid white;
          border-radius: 50%;
          animation: spin 1s linear infinite;
          margin-bottom: 20px;
        }

        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }

        @keyframes slideDown {
          from {
            opacity: 0;
            transform: translateY(-30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @keyframes shake {
          0%, 100% { transform: translateX(0); }
          25% { transform: translateX(-5px); }
          75% { transform: translateX(5px); }
        }
      `}</style>

      <div className="dashboard-content">
        <div className="dashboard-header">
          <h1>Welcome back, {user.name}!</h1>
          <p>Manage your expense groups and track shared costs</p>
        </div>

        {error && (
          <div className="error-message">
            <strong>Error:</strong> {error}
          </div>
        )}

        <div className="dashboard-grid">
          {/* My Groups Card */}
          <div className="dashboard-card">
            <div className="card-header">
              <h3>My Groups</h3>
              <Link to="/groups" className="btn-primary">
                Manage Groups
              </Link>
            </div>
            <ul className="group-list">
              {groups.slice(0, 5).map((group, index) => (
                <li key={group._id} className="group-item" style={{animationDelay: `${index * 0.1}s`}}>
                  <div className="group-name">{group.name}</div>
                  <div className="group-meta">
                    {group.members?.filter(m => m.status === 'accepted').length || 0} active members
                    {group.members?.filter(m => m.status === 'pending').length > 0 && (
                      <span className="status-badge status-pending">
                        {group.members.filter(m => m.status === 'pending').length} pending
                      </span>
                    )}
                    {group.rejectedInvites?.length > 0 && (
                      <span className="status-badge status-rejected">
                        {group.rejectedInvites.length} rejected
                      </span>
                    )}
                  </div>
                  <div className="btn-group">
                    <Link to={`/reports/${group._id}`} className="btn-secondary">
                      View Report
                    </Link>
                  </div>
                </li>
              ))}
              {groups.length === 0 && (
                <div className="empty-state">
                  <i>ðŸ“Š</i>
                  <p>No groups yet. Create your first group!</p>
                  <Link to="/groups" className="btn-primary" style={{marginTop: '15px'}}>
                    Create Group
                  </Link>
                </div>
              )}
            </ul>
          </div>

          {/* Pending Invites Card */}
          <div className="dashboard-card">
            <div className="card-header">
              <h3>Pending Invites</h3>
            </div>
            <ul className="invite-list">
              {pendingInvites.map((group, index) => (
                <li key={group._id} className="invite-item" style={{animationDelay: `${index * 0.1}s`}}>
                  <div className="group-name">{group.name}</div>
                  <div className="group-meta">
                    Invited by {group.createdBy?.name}
                  </div>
                  {group.description && (
                    <p style={{color: '#718096', fontSize: '0.9rem', marginBottom: '15px'}}>
                      {group.description}
                    </p>
                  )}
                  <div className="btn-group">
                    <button
                      onClick={() => handleInviteResponse(group._id, 'accept')}
                      className="btn-success"
                    >
                      Accept
                    </button>
                    <button
                      onClick={() => handleInviteResponse(group._id, 'reject')}
                      className="btn-danger"
                    >
                      Reject
                    </button>
                  </div>
                </li>
              ))}
              {pendingInvites.length === 0 && (
                <div className="empty-state">
                  <i>ðŸ“¨</i>
                  <p>No pending invites</p>
                </div>
              )}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
