import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../utils/api';

const GroupsPage = ({ user }) => {
  const [groups, setGroups] = useState([]);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showAddMemberModal, setShowAddMemberModal] = useState(false);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [newGroup, setNewGroup] = useState({
    name: '',
    description: '',
    memberEmails: []
  });
  const [newMemberEmails, setNewMemberEmails] = useState([]);
  const [loading, setLoading] = useState(false);
  const [addMemberLoading, setAddMemberLoading] = useState(false);

  useEffect(() => {
    fetchGroups();
  }, []);

  const fetchGroups = async () => {
    try {
      const response = await api.get('/groups');
      setGroups(response.data);
    } catch (error) {
      console.error('Error fetching groups:', error);
    }
  };

  const handleSearchUsers = async (query) => {
    if (query.length < 2) {
      setSearchResults([]);
      return;
    }

    try {
      const response = await api.get(`/groups/search/users?query=${query}`);
      setSearchResults(response.data);
    } catch (error) {
      console.error('Error searching users:', error);
    }
  };

  const handleCreateGroup = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await api.post('/groups', newGroup);
      setShowCreateModal(false);
      setNewGroup({ name: '', description: '', memberEmails: [] });
      fetchGroups();
    } catch (error) {
      console.error('Error creating group:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddMembers = async (e) => {
    e.preventDefault();
    if (!selectedGroup || newMemberEmails.length === 0) return;

    setAddMemberLoading(true);

    try {
      await api.post(`/groups/${selectedGroup._id}/add-members`, {
        memberEmails: newMemberEmails
      });
      
      setShowAddMemberModal(false);
      setSelectedGroup(null);
      setNewMemberEmails([]);
      setSearchQuery('');
      setSearchResults([]);
      fetchGroups();
    } catch (error) {
      console.error('Error adding members:', error);
    } finally {
      setAddMemberLoading(false);
    }
  };

  const openAddMemberModal = (group) => {
    setSelectedGroup(group);
    setShowAddMemberModal(true);
  };

  const closeAddMemberModal = () => {
    setShowAddMemberModal(false);
    setSelectedGroup(null);
    setNewMemberEmails([]);
    setSearchQuery('');
    setSearchResults([]);
  };

  const addMemberEmail = (email) => {
    if (!newMemberEmails.includes(email)) {
      setNewMemberEmails([...newMemberEmails, email]);
    }
    setSearchQuery('');
    setSearchResults([]);
  };

  const removeMemberEmail = (email) => {
    setNewMemberEmails(newMemberEmails.filter(e => e !== email));
  };

  const addMemberEmailToNewGroup = (email) => {
    if (!newGroup.memberEmails.includes(email)) {
      setNewGroup({
        ...newGroup,
        memberEmails: [...newGroup.memberEmails, email]
      });
    }
    setSearchQuery('');
    setSearchResults([]);
  };

  const removeMemberEmailFromNewGroup = (email) => {
    setNewGroup({
      ...newGroup,
      memberEmails: newGroup.memberEmails.filter(e => e !== email)
    });
  };

  const compareIds = (id1, id2) => {
    if (!id1 || !id2) return false;
    return id1.toString() === id2.toString();
  };

  const isGroupCreator = (group) => {
    return compareIds(group.createdBy?._id, user.id);
  };

  return (
    <div className="groups-container">
      <style jsx>{`
        .groups-container {
          min-height: 100vh;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          padding: 20px;
        }

        .groups-content {
          max-width: 1400px;
          margin: 0 auto;
        }

        .groups-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 40px;
          animation: slideDown 0.6s ease-out;
        }

        .groups-header h1 {
          color: white;
          font-size: 2.5rem;
          font-weight: 700;
          margin: 0;
        }

        .btn-create {
          background: linear-gradient(135deg, #48bb78, #38a169);
          color: white;
          padding: 15px 30px;
          border-radius: 15px;
          text-decoration: none;
          font-weight: 600;
          border: none;
          cursor: pointer;
          transition: all 0.3s ease;
          box-shadow: 0 10px 20px rgba(72, 187, 120, 0.3);
        }

        .btn-create:hover {
          transform: translateY(-3px);
          box-shadow: 0 15px 30px rgba(72, 187, 120, 0.4);
        }

        .groups-grid {
          display: grid;
          grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
          gap: 30px;
          margin-bottom: 40px;
        }

        .group-card {
          background: white;
          border-radius: 20px;
          padding: 30px;
          box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
          transition: all 0.3s ease;
          animation: fadeInUp 0.6s ease-out;
          border: 1px solid rgba(255, 255, 255, 0.2);
          backdrop-filter: blur(10px);
        }

        .group-card:hover {
          transform: translateY(-8px);
          box-shadow: 0 30px 60px rgba(0, 0, 0, 0.15);
        }

        .group-header {
          display: flex;
          justify-content: space-between;
          align-items: flex-start;
          margin-bottom: 20px;
        }

        .group-name {
          font-size: 1.4rem;
          font-weight: 700;
          color: #2d3748;
          margin: 0;
        }

        .group-creator {
          background: #e2e8f0;
          color: #4a5568;
          padding: 4px 12px;
          border-radius: 20px;
          font-size: 0.8rem;
          font-weight: 600;
        }

        .group-description {
          color: #718096;
          margin-bottom: 25px;
          line-height: 1.5;
        }

        .members-section {
          margin-bottom: 25px;
        }

        .members-title {
          font-size: 0.9rem;
          font-weight: 600;
          color: #4a5568;
          margin-bottom: 15px;
        }

        .member-item {
          display: flex;
          align-items: center;
          padding: 8px 0;
          border-bottom: 1px solid #f7fafc;
        }

        .member-status {
          width: 8px;
          height: 8px;
          border-radius: 50%;
          margin-right: 12px;
        }

        .status-accepted { background: #48bb78; }
        .status-pending { background: #ed8936; }
        .status-rejected { background: #f56565; }

        .member-name {
          flex: 1;
          color: #4a5568;
          font-size: 0.9rem;
        }

        .you-badge {
          background: #bee3f8;
          color: #2b6cb0;
          padding: 2px 8px;
          border-radius: 10px;
          font-size: 0.7rem;
          margin-left: 8px;
        }

        .creator-badge {
          background: #fefcbf;
          color: #744210;
          padding: 2px 8px;
          border-radius: 10px;
          font-size: 0.7rem;
          margin-left: 8px;
        }

        .group-stats {
          display: flex;
          justify-content: space-between;
          color: #a0aec0;
          font-size: 0.9rem;
          margin-bottom: 25px;
          padding-top: 20px;
          border-top: 2px solid #f7fafc;
        }

        .group-actions {
          display: flex;
          gap: 12px;
        }

        .btn-report {
          flex: 1;
          background: linear-gradient(135deg, #667eea, #764ba2);
          color: white;
          padding: 12px;
          border-radius: 12px;
          text-decoration: none;
          text-align: center;
          font-weight: 600;
          transition: all 0.3s ease;
        }

        .btn-report:hover {
          transform: translateY(-2px);
          box-shadow: 0 8px 20px rgba(102, 126, 234, 0.3);
        }

        .btn-add-member {
          background: linear-gradient(135deg, #48bb78, #38a169);
          color: white;
          padding: 12px 20px;
          border-radius: 12px;
          border: none;
          cursor: pointer;
          font-weight: 600;
          transition: all 0.3s ease;
        }

        .btn-add-member:hover {
          transform: translateY(-2px);
          box-shadow: 0 8px 20px rgba(72, 187, 120, 0.3);
        }

        .empty-state {
          text-align: center;
          padding: 80px 20px;
          grid-column: 1 / -1;
        }

        .empty-icon {
          font-size: 4rem;
          margin-bottom: 20px;
          opacity: 0.7;
        }

        .empty-text {
          color: white;
          font-size: 1.2rem;
          margin-bottom: 30px;
        }

        /* Modal Styles */
        .modal-overlay {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          background: rgba(0, 0, 0, 0.7);
          display: flex;
          align-items: center;
          justify-content: center;
          z-index: 1000;
          padding: 20px;
          backdrop-filter: blur(5px);
        }

        .modal-content {
          background: white;
          border-radius: 20px;
          padding: 40px;
          max-width: 500px;
          width: 100%;
          max-height: 90vh;
          overflow-y: auto;
          box-shadow: 0 30px 60px rgba(0, 0, 0, 0.3);
          animation: modalSlideIn 0.3s ease-out;
        }

        .modal-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 30px;
        }

        .modal-title {
          font-size: 1.5rem;
          font-weight: 700;
          color: #2d3748;
          margin: 0;
        }

        .close-button {
          background: none;
          border: none;
          font-size: 1.5rem;
          color: #a0aec0;
          cursor: pointer;
          transition: color 0.3s ease;
        }

        .close-button:hover {
          color: #718096;
        }

        .form-group {
          margin-bottom: 25px;
        }

        .form-label {
          display: block;
          margin-bottom: 8px;
          font-weight: 600;
          color: #4a5568;
        }

        .form-input {
          width: 100%;
          padding: 15px;
          border: 2px solid #e2e8f0;
          border-radius: 12px;
          font-size: 1rem;
          transition: all 0.3s ease;
        }

        .form-input:focus {
          outline: none;
          border-color: #667eea;
          box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
        }

        .search-results {
          max-height: 150px;
          overflow-y: auto;
          border: 2px solid #e2e8f0;
          border-radius: 12px;
          margin-top: 5px;
        }

        .search-result-item {
          padding: 12px 15px;
          cursor: pointer;
          border-bottom: 1px solid #f7fafc;
          transition: background 0.3s ease;
        }

        .search-result-item:hover {
          background: #f7fafc;
        }

        .email-tags {
          display: flex;
          flex-wrap: wrap;
          gap: 8px;
          margin-top: 10px;
        }

        .email-tag {
          background: linear-gradient(135deg, #667eea, #764ba2);
          color: white;
          padding: 6px 12px;
          border-radius: 20px;
          font-size: 0.8rem;
          display: flex;
          align-items: center;
          gap: 6px;
        }

        .remove-tag {
          background: none;
          border: none;
          color: white;
          cursor: pointer;
          font-size: 1rem;
          padding: 0;
        }

        .modal-actions {
          display: flex;
          gap: 15px;
          justify-content: flex-end;
          margin-top: 30px;
          padding-top: 20px;
          border-top: 2px solid #f7fafc;
        }

        .btn-cancel {
          background: #e2e8f0;
          color: #4a5568;
          padding: 12px 24px;
          border-radius: 12px;
          border: none;
          cursor: pointer;
          font-weight: 600;
          transition: all 0.3s ease;
        }

        .btn-cancel:hover {
          background: #cbd5e0;
        }

        .btn-submit {
          background: linear-gradient(135deg, #667eea, #764ba2);
          color: white;
          padding: 12px 24px;
          border-radius: 12px;
          border: none;
          cursor: pointer;
          font-weight: 600;
          transition: all 0.3s ease;
        }

        .btn-submit:hover:not(:disabled) {
          transform: translateY(-2px);
          box-shadow: 0 8px 20px rgba(102, 126, 234, 0.3);
        }

        .btn-submit:disabled {
          opacity: 0.6;
          cursor: not-allowed;
        }

        @keyframes slideDown {
          from {
            opacity: 0;
            transform: translateY(-30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @keyframes modalSlideIn {
          from {
            opacity: 0;
            transform: scale(0.9) translateY(-20px);
          }
          to {
            opacity: 1;
            transform: scale(1) translateY(0);
          }
        }
      `}</style>

      <div className="groups-content">
        <div className="groups-header">
          <h1>My Groups</h1>
          <button
            onClick={() => setShowCreateModal(true)}
            className="btn-create"
          >
            + Create New Group
          </button>
        </div>

        <div className="groups-grid">
          {groups.map((group, index) => (
            <div key={group._id} className="group-card" style={{animationDelay: `${index * 0.1}s`}}>
              <div className="group-header">
                <h3 className="group-name">{group.name}</h3>
                {isGroupCreator(group) && (
                  <span className="group-creator">Creator</span>
                )}
              </div>
              
              {group.description && (
                <p className="group-description">{group.description}</p>
              )}

              <div className="members-section">
                <div className="members-title">Members</div>
                {group.members?.map((member) => (
                  <div key={member.user._id} className="member-item">
                    <div className={`member-status status-${member.status}`}></div>
                    <span className="member-name">
                      {member.user.name}
                      {compareIds(member.user._id, user.id) && (
                        <span className="you-badge">You</span>
                      )}
                      {compareIds(member.user._id, group.createdBy._id) && (
                        <span className="creator-badge">Creator</span>
                      )}
                    </span>
                  </div>
                ))}
              </div>

              <div className="group-stats">
                <span>{group.members?.filter(m => m.status === 'accepted').length || 0} active</span>
                <span>{group.expenses?.length || 0} expenses</span>
              </div>

              <div className="group-actions">
                <Link to={`/reports/${group._id}`} className="btn-report">
                  View Report
                </Link>
                {isGroupCreator(group) && (
                  <button
                    onClick={() => openAddMemberModal(group)}
                    className="btn-add-member"
                    title="Add more members"
                  >
                    +
                  </button>
                )}
              </div>
            </div>
          ))}

          {groups.length === 0 && (
            <div className="empty-state">
              <div className="empty-icon"></div>
              <div className="empty-text">No groups yet</div>
              <button
                onClick={() => setShowCreateModal(true)}
                className="btn-create"
              >
                Create Your First Group
              </button>
            </div>
          )}
        </div>

        {/* Create Group Modal */}
        {showCreateModal && (
          <div className="modal-overlay">
            <div className="modal-content">
              <div className="modal-header">
                <h3 className="modal-title">Create New Group</h3>
                <button
                  onClick={() => setShowCreateModal(false)}
                  className="close-button"
                >
                  
                </button>
              </div>
              
              <form onSubmit={handleCreateGroup}>
                <div className="form-group">
                  <label className="form-label">Group Name</label>
                  <input
                    type="text"
                    required
                    className="form-input"
                    value={newGroup.name}
                    onChange={(e) => setNewGroup({ ...newGroup, name: e.target.value })}
                    placeholder="Enter group name"
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Description</label>
                  <textarea
                    className="form-input"
                    rows="3"
                    value={newGroup.description}
                    onChange={(e) => setNewGroup({ ...newGroup, description: e.target.value })}
                    placeholder="Enter group description (optional)"
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Invite Members by Email</label>
                  <input
                    type="text"
                    className="form-input"
                    placeholder="Search by email..."
                    value={searchQuery}
                    onChange={(e) => {
                      setSearchQuery(e.target.value);
                      handleSearchUsers(e.target.value);
                    }}
                  />
                  
                  {searchResults.length > 0 && (
                    <div className="search-results">
                      {searchResults.map((user) => (
                        <div
                          key={user._id}
                          className="search-result-item"
                          onClick={() => addMemberEmailToNewGroup(user.email)}
                        >
                          {user.name} ({user.email})
                        </div>
                      ))}
                    </div>
                  )}

                  {newGroup.memberEmails.length > 0 && (
                    <div className="email-tags">
                      {newGroup.memberEmails.map((email) => (
                        <span key={email} className="email-tag">
                          {email}
                          <button
                            type="button"
                            className="remove-tag"
                            onClick={() => removeMemberEmailFromNewGroup(email)}
                          >
                            
                          </button>
                        </span>
                      ))}
                    </div>
                  )}
                </div>

                <div className="modal-actions">
                  <button
                    type="button"
                    onClick={() => setShowCreateModal(false)}
                    className="btn-cancel"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    disabled={loading}
                    className="btn-submit"
                  >
                    {loading ? 'Creating...' : 'Create Group'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Add Members Modal */}
        {showAddMemberModal && selectedGroup && (
          <div className="modal-overlay">
            <div className="modal-content">
              <div className="modal-header">
                <h3 className="modal-title">Add Members to {selectedGroup.name}</h3>
                <button
                  onClick={closeAddMemberModal}
                  className="close-button"
                >
                  
                </button>
              </div>
              
              <form onSubmit={handleAddMembers}>
                <div className="form-group">
                  <label className="form-label">Invite Members by Email</label>
                  <input
                    type="text"
                    className="form-input"
                    placeholder="Search by email..."
                    value={searchQuery}
                    onChange={(e) => {
                      setSearchQuery(e.target.value);
                      handleSearchUsers(e.target.value);
                    }}
                  />
                  
                  {searchResults.length > 0 && (
                    <div className="search-results">
                      {searchResults.map((user) => (
                        <div
                          key={user._id}
                          className="search-result-item"
                          onClick={() => addMemberEmail(user.email)}
                        >
                          {user.name} ({user.email})
                        </div>
                      ))}
                    </div>
                  )}

                  {newMemberEmails.length > 0 && (
                    <div className="email-tags">
                      {newMemberEmails.map((email) => (
                        <span key={email} className="email-tag">
                          {email}
                          <button
                            type="button"
                            className="remove-tag"
                            onClick={() => removeMemberEmail(email)}
                          >
                            
                          </button>
                        </span>
                      ))}
                    </div>
                  )}
                </div>

                <div className="modal-actions">
                  <button
                    type="button"
                    onClick={closeAddMemberModal}
                    className="btn-cancel"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    disabled={addMemberLoading || newMemberEmails.length === 0}
                    className="btn-submit"
                  >
                    {addMemberLoading ? 'Adding...' : 'Add Members'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default GroupsPage;
