    <div className="dashboard-container">
      <div className="dashboard-header">
        <div className="header-content">
          <h1>Welcome back, {user.name}</h1>
          <p>Manage your expense groups and track shared costs</p>
        </div>
        <div className="header-stats">
          <div className="stat-card">
            <div className="stat-icon groups"></div>
            <div className="stat-info">
              <span className="stat-number">{groups.length}</span>
              <span className="stat-label">Active Groups</span>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon invites"></div>
            <div className="stat-info">
              <span className="stat-number">{pendingInvites.length}</span>
              <span className="stat-label">Pending Invites</span>
            </div>
          </div>
        </div>
      </div>

      {error && (
        <div className="error-message">
          <div className="error-icon"></div>
          <span>{error}</span>
        </div>
      )}

      <div className="dashboard-content">
        <div className="content-section">
          <div className="section-header">
            <h2>Profile Overview</h2>
          </div>
          <div className="profile-card">
            <div className="profile-info">
              <div className="info-item">
                <label>Full Name</label>
                <span>{user.name}</span>
              </div>
              <div className="info-item">
                <label>Email Address</label>
                <span>{user.email}</span>
              </div>
              <div className="info-item">
                <label>Mobile Number</label>
                <span>{user.mobile}</span>
              </div>
            </div>
          </div>
        </div>

        <div className="content-grid">
          <div className="main-content">
            <div className="section-tabs">
              <button 
                className={`tab-button ${activeTab === 'groups' ? 'active' : ''}`}
                onClick={() => setActiveTab('groups')}
              >
                My Groups
              </button>
              <button 
                className={`tab-button ${activeTab === 'invites' ? 'active' : ''}`}
                onClick={() => setActiveTab('invites')}
              >
                Pending Invites
                {pendingInvites.length > 0 && (
                  <span className="notification-badge">{pendingInvites.length}</span>
                )}
              </button>
            </div>

            <div className="tab-content">
              {activeTab === 'groups' && (
                <div className="groups-section">
                  <div className="section-header">
                    <h3>Your Expense Groups</h3>
                    <Link to="/groups" className="primary-button">
                      Manage Groups
                    </Link>
                  </div>
                  <div className="cards-grid">
                    {groups.slice(0, 6).map((group) => (
                      <div key={group._id} className="group-card">
                        <div className="card-header">
                          <h4>{group.name}</h4>
                          <div className="member-count">
                            {group.members?.filter(m => m.status === 'accepted').length || 0} members
                          </div>
                        </div>
                        <div className="card-content">
                          <div className="status-indicators">
                            {group.members?.filter(m => m.status === 'pending').length > 0 && (
                              <span className="status pending">
                                {group.members.filter(m => m.status === 'pending').length} pending
                              </span>
                            )}
                            {group.rejectedInvites?.length > 0 && (
                              <span className="status rejected">
                                {group.rejectedInvites.length} rejected
                              </span>
                            )}
                          </div>
                          {group.createdBy && (
                            <p className="creator-info">
                              Created by {group.createdBy.name}
                              {compareIds(group.createdBy._id, user.id) && ' (You)'}
                            </p>
                          )}
                        </div>
                        <div className="card-actions">
                          <Link 
                            to={`/reports/${group._id}`} 
                            className="action-button primary"
                          >
                            View Report
                          </Link>
                        </div>
                      </div>
                    ))}
                    {groups.length === 0 && (
                      <div className="empty-state">
                        <div className="empty-icon"></div>
                        <h4>No groups yet</h4>
                        <p>Create your first group to start tracking expenses</p>
                        <Link to="/groups" className="primary-button">
                          Create Group
                        </Link>
                      </div>
                    )}
                  </div>
                </div>
              )}

              {activeTab === 'invites' && (
                <div className="invites-section">
                  <div className="section-header">
                    <h3>Group Invitations</h3>
                  </div>
                  <div className="invites-list">
                    {pendingInvites.map((group) => (
                      <div key={group._id} className="invite-card">
                        <div className="invite-content">
                          <div className="invite-header">
                            <h4>{group.name}</h4>
                            <span className="invite-badge">New Invite</span>
                          </div>
                          <p className="invite-description">
                            Invited by {group.createdBy?.name}
                          </p>
                          {group.description && (
                            <p className="group-description">{group.description}</p>
                          )}
                        </div>
                        <div className="invite-actions">
                          <button
                            onClick={() => handleInviteResponse(group._id, 'accept')}
                            className="action-button success"
                          >
                            Accept
                          </button>
                          <button
                            onClick={() => handleInviteResponse(group._id, 'reject')}
                            className="action-button danger"
                          >
                            Decline
                          </button>
                        </div>
                      </div>
                    ))}
                    {pendingInvites.length === 0 && (
                      <div className="empty-state">
                        <div className="empty-icon"></div>
                        <h4>No pending invites</h4>
                        <p>You're all caught up! New group invites will appear here.</p>
                      </div>
                    )}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
