import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import API from '../utils/api';

const ReportsPage = () => {
  const { groupId } = useParams();
  const [group, setGroup] = useState(null);
  const [expenses, setExpenses] = useState([]);
  const [settlements, setSettlements] = useState(null);
  const [showAddExpense, setShowAddExpense] = useState(false);
  const [newExpense, setNewExpense] = useState({
    description: '',
    amount: '',
    splitType: 'equal'
  });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchGroupData();
  }, [groupId]);

  const fetchGroupData = async () => {
    try {
      const [expensesResponse, settlementsResponse] = await Promise.all([
        API.get(`/expenses/group/${groupId}`),
        API.get(`/expenses/settlements/${groupId}`)
      ]);

      setExpenses(expensesResponse.data);
      setSettlements(settlementsResponse.data);
    } catch (error) {
      console.error('Error fetching group data:', error);
    }
  };

  const handleAddExpense = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const expenseData = {
        ...newExpense,
        groupId,
        amount: parseFloat(newExpense.amount),
        splits: calculateSplits()
      };

      await API.post('/expenses', expenseData);
      setShowAddExpense(false);
      setNewExpense({ description: '', amount: '', splitType: 'equal' });
      fetchGroupData();
    } catch (error) {
      console.error('Error adding expense:', error);
    } finally {
      setLoading(false);
    }
  };

  const calculateSplits = () => {
    if (!settlements) return [];

    const amount = parseFloat(newExpense.amount);
    const members = settlements.balances.map(b => b.user);

    if (newExpense.splitType === 'equal') {
      const splitAmount = amount / members.length;
      return members.map(member => ({
        user: member._id,
        amount: splitAmount
      }));
    }

    // For custom splits, you would implement percentage-based splitting here
    return [];
  };

  const handleMarkAsPaid = async (expenseId, userId) => {
    try {
      await API.put(`/expenses/${expenseId}/pay`, { userId });
      fetchGroupData();
    } catch (error) {
      console.error('Error marking payment:', error);
    }
  };

  if (!settlements) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-lg">Loading...</div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold text-gray-900">Group Report</h1>
          <button
            onClick={() => setShowAddExpense(true)}
            className="bg-primary-600 text-white px-4 py-2 rounded-md hover:bg-primary-700"
          >
            Add Expense
          </button>
        </div>

        {/* Settlements Summary */}
        <div className="bg-white shadow rounded-lg mb-6">
          <div className="px-4 py-5 sm:px-6">
            <h3 className="text-lg leading-6 font-medium text-gray-900">Settlement Summary</h3>
          </div>
          <div className="border-t border-gray-200 px-4 py-5 sm:p-6">
            {settlements.settlements.length === 0 ? (
              <p className="text-gray-500">All expenses are settled up! üéâ</p>
            ) : (
              <div className="space-y-3">
                {settlements.settlements.map((settlement, index) => (
                  <div key={index} className="flex items-center justify-between p-3 bg-gray-50 rounded">
                    <span className="text-sm">
                      <strong>{settlement.from.name}</strong> owes{' '}
                      <strong>{settlement.to.name}</strong>
                    </span>
                    <div className="flex items-center space-x-3">
                      <span className="font-medium">‚Çπ{settlement.amount.toFixed(2)}</span>
                      <button
                        onClick={() => {/* Implement payment */}}
                        className="bg-green-600 text-white px-3 py-1 rounded text-sm hover:bg-green-700"
                      >
                        Pay Now
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Expenses List */}
        <div className="bg-white shadow rounded-lg">
          <div className="px-4 py-5 sm:px-6">
            <h3 className="text-lg leading-6 font-medium text-gray-900">Expenses</h3>
          </div>
          <div className="border-t border-gray-200">
            {expenses.length === 0 ? (
              <div className="px-4 py-5 sm:p-6 text-center text-gray-500">
                No expenses added yet.
              </div>
            ) : (
              <div className="divide-y divide-gray-200">
                {expenses.map((expense) => (
                  <div key={expense._id} className="px-4 py-4">
                    <div className="flex justify-between items-start mb-2">
                      <div>
                        <h4 className="text-sm font-medium text-gray-900">{expense.description}</h4>
                        <p className="text-sm text-gray-500">
                          Paid by {expense.paidBy.name} ‚Ä¢ ‚Çπ{expense.amount}
                        </p>
                      </div>
                      <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
                        {expense.splitType === 'equal' ? 'Equal Split' : 'Custom Split'}
                      </span>
                    </div>
                    <div className="mt-2">
                      <h5 className="text-xs font-medium text-gray-500 mb-1">Splits:</h5>
                      <div className="space-y-1">
                        {expense.splits.map((split, index) => (
                          <div key={index} className="flex justify-between items-center text-sm">
                            <span>{split.user.name}</span>
                            <div className="flex items-center space-x-2">
                              <span>‚Çπ{split.amount.toFixed(2)}</span>
                              {split.paid ? (
                                <span className="text-green-600">‚úÖ Paid</span>
                              ) : (
                                <button
                                  onClick={() => handleMarkAsPaid(expense._id, split.user._id)}
                                  className="text-red-600 hover:text-red-700"
                                >
                                  ‚ùå Mark Paid
                                </button>
                              )}
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Add Expense Modal */}
        {showAddExpense && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
            <div className="bg-white rounded-lg max-w-md w-full p-6">
              <h2 className="text-xl font-bold mb-4">Add New Expense</h2>
              <form onSubmit={handleAddExpense}>
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Description
                    </label>
                    <input
                      type="text"
                      required
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                      value={newExpense.description}
                      onChange={(e) => setNewExpense({ ...newExpense, description: e.target.value })}
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Amount (‚Çπ)
                    </label>
                    <input
                      type="number"
                      step="0.01"
                      required
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                      value={newExpense.amount}
                      onChange={(e) => setNewExpense({ ...newExpense, amount: e.target.value })}
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Split Type
                    </label>
                    <select
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-primary-500 focus:border-primary-500"
                      value={newExpense.splitType}
                      onChange={(e) => setNewExpense({ ...newExpense, splitType: e.target.value })}
                    >
                      <option value="equal">Equal Split</option>
                      <option value="custom">Custom Split</option>
                    </select>
                  </div>
                </div>
                <div className="mt-6 flex justify-end space-x-3">
                  <button
                    type="button"
                    onClick={() => setShowAddExpense(false)}
                    className="px-4 py-2 text-sm font-medium text-gray-700 hover:text-gray-900"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    disabled={loading}
                    className="px-4 py-2 text-sm font-medium text-white bg-primary-600 rounded-md hover:bg-primary-700 disabled:opacity-50"
                  >
                    {loading ? 'Adding...' : 'Add Expense'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ReportsPage;
