import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import api from '../utils/api';

const Signup = ({ onLogin }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    mobile: '',
    password: '',
    confirmPassword: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      setLoading(false);
      return;
    }

    try {
      const response = await api.post('/auth/signup', {
        name: formData.name,
        email: formData.email,
        mobile: formData.mobile,
        password: formData.password
      });

      onLogin(response.data.user, response.data.token);
    } catch (error) {
      setError(error.response?.data?.message || 'Signup failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <style jsx>{`
        .auth-container {
          min-height: 100vh;
          background: linear-gradient(135deg, #48bb78 0%, #38a169 100%);
          display: flex;
          align-items: center;
          justify-content: center;
          padding: 20px;
        }

        .auth-card {
          background: white;
          border-radius: 25px;
          padding: 50px;
          box-shadow: 0 30px 60px rgba(0, 0, 0, 0.2);
          width: 100%;
          max-width: 500px;
          backdrop-filter: blur(10px);
          border: 1px solid rgba(255, 255, 255, 0.2);
          animation: cardSlideIn 0.6s ease-out;
        }

        .auth-header {
          text-align: center;
          margin-bottom: 40px;
        }

        .auth-icon {
          width: 80px;
          height: 80px;
          background: linear-gradient(135deg, #48bb78, #38a169);
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
          margin: 0 auto 20px;
          font-size: 2rem;
          color: white;
        }

        .auth-title {
          font-size: 2.2rem;
          font-weight: 700;
          color: #2d3748;
          margin-bottom: 10px;
        }

        .auth-subtitle {
          color: #718096;
          font-size: 1rem;
        }

        .auth-form {
          space-y: 20px;
        }

        .form-group {
          margin-bottom: 20px;
        }

        .form-label {
          display: block;
          margin-bottom: 8px;
          font-weight: 600;
          color: #4a5568;
          font-size: 0.9rem;
        }

        .form-input {
          width: 100%;
          padding: 15px 20px;
          border: 2px solid #e2e8f0;
          border-radius: 12px;
          font-size: 1rem;
          transition: all 0.3s ease;
          background: #f8fafc;
        }

        .form-input:focus {
          outline: none;
          border-color: #48bb78;
          background: white;
          box-shadow: 0 0 0 3px rgba(72, 187, 120, 0.1);
          transform: translateY(-2px);
        }

        .form-input::placeholder {
          color: #a0aec0;
        }

        .error-message {
          background: #fed7d7;
          color: #c53030;
          padding: 15px 20px;
          border-radius: 12px;
          margin-bottom: 25px;
          border-left: 4px solid #f56565;
          animation: shake 0.5s ease-in-out;
        }

        .submit-button {
          width: 100%;
          background: linear-gradient(135deg, #48bb78, #38a169);
          color: white;
          padding: 18px;
          border: none;
          border-radius: 15px;
          font-size: 1.1rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s ease;
          margin-bottom: 25px;
          position: relative;
          overflow: hidden;
        }

        .submit-button:hover:not(:disabled) {
          transform: translateY(-3px);
          box-shadow: 0 15px 30px rgba(72, 187, 120, 0.4);
        }

        .submit-button:disabled {
          opacity: 0.7;
          cursor: not-allowed;
          transform: none;
        }

        .submit-button:active {
          transform: translateY(-1px);
        }

        .button-loading {
          position: relative;
          color: transparent;
        }

        .button-loading::after {
          content: '';
          position: absolute;
          width: 20px;
          height: 20px;
          top: 50%;
          left: 50%;
          margin-left: -10px;
          margin-top: -10px;
          border: 2px solid transparent;
          border-top: 2px solid white;
          border-radius: 50%;
          animation: buttonSpin 0.8s linear infinite;
        }

        .auth-footer {
          text-align: center;
          padding-top: 25px;
          border-top: 2px solid #f7fafc;
        }

        .auth-link {
          color: #48bb78;
          text-decoration: none;
          font-weight: 600;
          transition: color 0.3s ease;
        }

        .auth-link:hover {
          color: #38a169;
        }

        @keyframes cardSlideIn {
          from {
            opacity: 0;
