import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../utils/api';

const GroupsPage = ({ user }) => {
  const [groups, setGroups] = useState([]);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showAddMemberModal, setShowAddMemberModal] = useState(false);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [newGroup, setNewGroup] = useState({
    name: '',
    description: '',
    memberEmails: []
  });
  const [newMemberEmails, setNewMemberEmails] = useState([]);
  const [loading, setLoading] = useState(false);
  const [addMemberLoading, setAddMemberLoading] = useState(false);

  useEffect(() => {
    fetchGroups();
  }, []);

  const fetchGroups = async () => {
    try {
      const response = await api.get('/groups');
      setGroups(response.data);
    } catch (error) {
      console.error('Error fetching groups:', error);
    }
  };

  const handleSearchUsers = async (query) => {
    if (query.length < 2) {
      setSearchResults([]);
      return;
    }

    try {
      const response = await api.get(`/groups/search/users?query=${query}`);
      setSearchResults(response.data);
    } catch (error) {
      console.error('Error searching users:', error);
    }
  };

  const handleCreateGroup = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      await api.post('/groups', newGroup);
      setShowCreateModal(false);
      setNewGroup({ name: '', description: '', memberEmails: [] });
      fetchGroups();
    } catch (error) {
      console.error('Error creating group:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddMembers = async (e) => {
    e.preventDefault();
    if (!selectedGroup || newMemberEmails.length === 0) return;

    setAddMemberLoading(true);

    try {
      await api.post(`/groups/${selectedGroup._id}/add-members`, {
        memberEmails: newMemberEmails
      });
      
      setShowAddMemberModal(false);
      setSelectedGroup(null);
      setNewMemberEmails([]);
      setSearchQuery('');
      setSearchResults([]);
      fetchGroups();
    } catch (error) {
      console.error('Error adding members:', error);
    } finally {
      setAddMemberLoading(false);
    }
  };

  const openAddMemberModal = (group) => {
    setSelectedGroup(group);
    setShowAddMemberModal(true);
  };

  const closeAddMemberModal = () => {
    setShowAddMemberModal(false);
    setSelectedGroup(null);
    setNewMemberEmails([]);
    setSearchQuery('');
    setSearchResults([]);
  };

  const addMemberEmail = (email) => {
    if (!newMemberEmails.includes(email)) {
      setNewMemberEmails([...newMemberEmails, email]);
    }
    setSearchQuery('');
    setSearchResults([]);
  };

  const removeMemberEmail = (email) => {
    setNewMemberEmails(newMemberEmails.filter(e => e !== email));
  };

  const addMemberEmailToNewGroup = (email) => {
    if (!newGroup.memberEmails.includes(email)) {
      setNewGroup({
        ...newGroup,
        memberEmails: [...newGroup.memberEmails, email]
      });
    }
    setSearchQuery('');
    setSearchResults([]);
  };

  const removeMemberEmailFromNewGroup = (email) => {
    setNewGroup({
      ...newGroup,
      memberEmails: newGroup.memberEmails.filter(e => e !== email)
    });
  };

  const compareIds = (id1, id2) => {
    if (!id1 || !id2) return false;
    return id1.toString() === id2.toString();
  };

  const isGroupCreator = (group) => {
    return compareIds(group.createdBy?._id, user.id);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        
        {/* Header */}
        <div className="mb-8 animate-slide-down">
          <div className="flex justify-between items-center">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 mb-2">My Groups</h1>
              <p className="text-gray-600">Manage your expense sharing groups</p>
            </div>
            <button
              onClick={() => setShowCreateModal(true)}
              className="bg-gradient-to-r from-blue-600 to-blue-700 text-white px-6 py-3 rounded-xl hover:from-blue-700 hover:to-blue-800 transition-all duration-300 shadow-sm hover:shadow-md flex items-center group"
            >
              <svg className="w-5 h-5 mr-2 group-hover:scale-110 transition-transform" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
              </svg>
              Create Group
            </button>
          </div>
        </div>

        {/* Groups Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {groups.map((group, index) => (
            <div
              key={group._id}
              className="bg-white rounded-2xl shadow-sm border border-gray-100 overflow-hidden hover:shadow-md transition-all duration-300 group animate-fade-in-up"
              style={{ animationDelay: `${index * 100}ms` }}
            >
              <div className="p-6">
                <div className="flex items-start justify-between mb-4">
                  <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-blue-600 rounded-xl flex items-center justify-center group-hover:scale-110 transition-transform duration-300">
                    <span className="text-white font-semibold text-lg">
                      {group.name.charAt(0).toUpperCase()}
                    </span>
                  </div>
                  {isGroupCreator(group) && (
                    <span className="px-2 py-1 bg-blue-100 text-blue-700 text-xs font-medium rounded-full">
                      Creator
                    </span>
                  )}
                </div>

                <h3 className="text-lg font-semibold text-gray-900 mb-2">{group.name}</h3>
                {group.description && (
                  <p className="text-gray-600 text-sm mb-4 line-clamp-2">{group.description}</p>
                )}

                {/* Members Summary */}
                <div className="space-y-3 mb-4">
                  <div className="flex items-center justify-between text-sm">
                    <span className="text-gray-600">Active Members</span>
                    <span className="font-medium text-gray-900">
                      {group.members?.filter(m => m.status === 'accepted').length || 0}
                    </span>
                  </div>
                  {group.members?.filter(m => m.status === 'pending').length > 0 && (
                    <div className="flex items-center justify-between text-sm">
                      <span className="text-amber-600">Pending</span>
                      <span className="font-medium text-amber-600">
                        {group.members.filter(m => m.status === 'pending').length}
                      </span>
                    </div>
                  )}
                  {group.rejectedInvites?.length > 0 && (
                    <div className="flex items-center justify-between text-sm">
                      <span className="text-red-600">Rejected</span>
                      <span className="font-medium text-red-600">
                        {group.rejectedInvites.length}
                      </span>
                    </div>
                  )}
                </div>

                {/* Action Buttons */}
                <div className="flex space-x-2">
                  <Link
                    to={`/reports/${group._id}`}
                    className="flex-1 bg-blue-600 text-white text-center py-2 px-3 rounded-lg hover:bg-blue-700 transition-all duration-300 text-sm font-medium"
                  >
                    View Report
                  </Link>
                  
                  {isGroupCreator(group) && (
                    <button
                      onClick={() => openAddMemberModal(group)}
                      className="p-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200 transition-all duration-300 group-hover:scale-105"
                      title="Add members"
                    >
                      <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                      </svg>
                    </button>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>

        {groups.length === 0 && (
          <div className="text-center py-16 animate-fade-in">
            <div className="w-24 h-24 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-6">
              <svg className="w-12 h-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z" />
              </svg>
            </div>
            <h3 className="text-xl font-semibold text-gray-900 mb-2">No groups yet</h3>
            <p className="text-gray-600 mb-6">Create your first group to start tracking expenses</p>
            <button
              onClick={() => setShowCreateModal(true)}
              className="bg-gradient-to-r from-blue-600 to-blue-700 text-white px-8 py-3 rounded-xl hover:from-blue-700 hover:to-blue-800 transition-all duration-300"
            >
              Create Your First Group
            </button>
          </div>
        )}

        {/* Create Group Modal */}
        {showCreateModal && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50 animate-fade-in">
            <div className="bg-white rounded-2xl shadow-xl max-w-md w-full animate-scale-in">
              <div className="p-6 border-b border-gray-100">
                <h3 className="text-xl font-semibold text-gray-900">Create New Group</h3>
              </div>
              
              <form onSubmit={handleCreateGroup} className="p-6">
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Group Name
                    </label>
                    <input
                      type="text"
                      required
                      className="w-full px-4 py-3 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300"
                      value={newGroup.name}
                      onChange={(e) => setNewGroup({ ...newGroup, name: e.target.value })}
                      placeholder="Enter group name"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Description
                    </label>
                    <textarea
                      className="w-full px-4 py-3 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300"
                      rows="3"
                      value={newGroup.description}
                      onChange={(e) => setNewGroup({ ...newGroup, description: e.target.value })}
                      placeholder="Group description (optional)"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Invite Members
                    </label>
                    <input
                      type="text"
                      className="w-full px-4 py-3 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300"
                      placeholder="Search by email..."
                      value={searchQuery}
                      onChange={(e) => {
                        setSearchQuery(e.target.value);
                        handleSearchUsers(e.target.value);
                      }}
                    />
                    
                    {searchResults.length > 0 && (
                      <div className="mt-2 border border-gray-200 rounded-xl bg-white max-h-32 overflow-y-auto animate-fade-in">
                        {searchResults.map((user) => (
                          <div
                            key={user._id}
                            className="px-4 py-3 hover:bg-gray-50 cursor-pointer text-sm border-b border-gray-100 last:border-b-0 transition-colors duration-200"
                            onClick={() => addMemberEmailToNewGroup(user.email)}
                          >
                            <div className="font-medium text-gray-900">{user.name}</div>
                            <div className="text-gray-500">{user.email}</div>
                          </div>
                        ))}
                      </div>
                    )}

                    {newGroup.memberEmails.length > 0 && (
                      <div className="mt-3 flex flex-wrap gap-2">
                        {newGroup.memberEmails.map((email) => (
                          <span
                            key={email}
                            className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800 animate-fade-in"
                          >
                            {email}
                            <button
                              type="button"
                              className="ml-2 hover:bg-blue-200 rounded-full transition-colors duration-200"
                              onClick={() => removeMemberEmailFromNewGroup(email)}
                            >
                              
                            </button>
                          </span>
                        ))}
                      </div>
                    )}
                  </div>
                </div>

                <div className="flex justify-end space-x-3 mt-6 pt-4 border-t border-gray-100">
                  <button
                    type="button"
                    onClick={() => setShowCreateModal(false)}
                    className="px-6 py-3 text-gray-700 bg-gray-100 rounded-xl hover:bg-gray-200 transition-all duration-300 font-medium"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    disabled={loading}
                    className="px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition-all duration-300 font-medium disabled:opacity-50 flex items-center"
                  >
                    {loading ? (
                      <>
                        <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                        Creating...
                      </>
                    ) : (
                      'Create Group'
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Add Members Modal */}
        {showAddMemberModal && selectedGroup && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50 animate-fade-in">
            <div className="bg-white rounded-2xl shadow-xl max-w-md w-full animate-scale-in">
              <div className="p-6 border-b border-gray-100">
                <h3 className="text-xl font-semibold text-gray-900">
                  Add Members to {selectedGroup.name}
                </h3>
              </div>
              
              <form onSubmit={handleAddMembers} className="p-6">
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Invite Members
                    </label>
                    <input
                      type="text"
                      className="w-full px-4 py-3 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300"
                      placeholder="Search by email..."
                      value={searchQuery}
                      onChange={(e) => {
                        setSearchQuery(e.target.value);
                        handleSearchUsers(e.target.value);
                      }}
                      disabled={addMemberLoading}
                    />
                    
                    {searchResults.length > 0 && (
                      <div className="mt-2 border border-gray-200 rounded-xl bg-white max-h-32 overflow-y-auto animate-fade-in">
                        {searchResults.map((user) => (
                          <div
                            key={user._id}
                            className="px-4 py-3 hover:bg-gray-50 cursor-pointer text-sm border-b border-gray-100 last:border-b-0 transition-colors duration-200"
                            onClick={() => addMemberEmail(user.email)}
                          >
                            <div className="font-medium text-gray-900">{user.name}</div>
                            <div className="text-gray-500">{user.email}</div>
                          </div>
                        ))}
                      </div>
                    )}

                    {newMemberEmails.length > 0 && (
                      <div className="mt-3 flex flex-wrap gap-2">
                        {newMemberEmails.map((email) => (
                          <span
                            key={email}
                            className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800 animate-fade-in"
                          >
                            {email}
                            <button
                              type="button"
                              className="ml-2 hover:bg-green-200 rounded-full transition-colors duration-200"
                              onClick={() => removeMemberEmail(email)}
                              disabled={addMemberLoading}
                            >
                              
                            </button>
                          </span>
                        ))}
                      </div>
                    )}
                  </div>
                </div>

                <div className="flex justify-end space-x-3 mt-6 pt-4 border-t border-gray-100">
                  <button
                    type="button"
                    onClick={closeAddMemberModal}
                    disabled={addMemberLoading}
                    className="px-6 py-3 text-gray-700 bg-gray-100 rounded-xl hover:bg-gray-200 transition-all duration-300 font-medium disabled:opacity-50"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    disabled={addMemberLoading || newMemberEmails.length === 0}
                    className="px-6 py-3 bg-green-600 text-white rounded-xl hover:bg-green-700 transition-all duration-300 font-medium disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
                  >
                    {addMemberLoading ? (
                      <>
                        <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                        Adding...
                      </>
                    ) : (
                      'Add Members'
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default GroupsPage;
