import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from '../utils/api';

const ReportsPage = ({ user }) => {
  const { groupId } = useParams();
  const [report, setReport] = useState(null);
  const [expenses, setExpenses] = useState([]);
  const [showAddExpense, setShowAddExpense] = useState(false);
  const [showAddMemberModal, setShowAddMemberModal] = useState(false);
  const [showCustomSplitModal, setShowCustomSplitModal] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [newExpense, setNewExpense] = useState({
    description: '',
    amount: '',
    splitType: 'equal'
  });
  const [customSplits, setCustomSplits] = useState([]);
  const [newMemberEmails, setNewMemberEmails] = useState([]);
  const [loading, setLoading] = useState(true);
  const [addExpenseLoading, setAddExpenseLoading] = useState(false);
  const [addMemberLoading, setAddMemberLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchReportData();
  }, [groupId]);

  const fetchReportData = async () => {
    try {
      setError('');
      const [reportResponse, expensesResponse] = await Promise.all([
        api.get(`/expenses/report/${groupId}`),
        api.get(`/expenses/group/${groupId}`)
      ]);
      
      setReport(reportResponse.data);
      setExpenses(expensesResponse.data);
    } catch (error) {
      console.error('Error fetching report data:', error);
      setError('Failed to load report data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleAddExpense = async (e) => {
    e.preventDefault();
    
    // Validation
    if (!newExpense.description.trim()) {
      setError('Please enter a description');
      return;
    }
    
    if (!newExpense.amount || parseFloat(newExpense.amount) <= 0) {
      setError('Please enter a valid amount');
      return;
    }

    setAddExpenseLoading(true);
    setError('');

    try {
      const expenseData = {
        description: newExpense.description.trim(),
        amount: parseFloat(newExpense.amount),
        groupId: groupId,
        splitType: newExpense.splitType
      };

      // Add custom splits if split type is custom
      if (newExpense.splitType === 'custom' && customSplits.length > 0) {
        expenseData.splits = customSplits;
      }

      const response = await api.post('/expenses', expenseData);
      
      // Reset form and close modal
      setNewExpense({ description: '', amount: '', splitType: 'equal' });
      setCustomSplits([]);
      setShowAddExpense(false);
      setShowCustomSplitModal(false);
      
      // Refresh data
      await fetchReportData();
      
    } catch (error) {
      console.error('Error adding expense:', error);
      const errorMessage = error.response?.data?.message || 
                          error.response?.data?.error || 
                          'Failed to add expense. Please try again.';
      setError(errorMessage);
    } finally {
      setAddExpenseLoading(false);
    }
  };

  const handleAddMembers = async (e) => {
    e.preventDefault();
    if (newMemberEmails.length === 0) return;

    setAddMemberLoading(true);
    setError('');

    try {
      await api.post(`/groups/${groupId}/add-members`, {
        memberEmails: newMemberEmails
      });
      
      setShowAddMemberModal(false);
      setNewMemberEmails([]);
      setSearchQuery('');
      setSearchResults([]);
      
      setError('Members invited successfully!');
      setTimeout(() => setError(''), 3000);
      
      fetchReportData();
      
    } catch (error) {
      console.error('Error adding members:', error);
      const errorMessage = error.response?.data?.message || 
                          'Failed to add members. Please try again.';
      setError(errorMessage);
    } finally {
      setAddMemberLoading(false);
    }
  };

  const handleSearchUsers = async (query) => {
    if (query.length < 2) {
      setSearchResults([]);
      return;
    }

    try {
      const response = await api.get(`/groups/search/users?query=${query}`);
      setSearchResults(response.data);
    } catch (error) {
      console.error('Error searching users:', error);
    }
  };

  const handleMarkAsPaid = async (expenseId, userId) => {
    try {
      await api.post('/expenses/mark-paid', { expenseId, userId });
      fetchReportData();
    } catch (error) {
      console.error('Error marking as paid:', error);
      setError('Failed to mark as paid. Please try again.');
    }
  };

  const handlePaySettlement = async (fromUserId, toUserId, amount) => {
    try {
      const response = await api.post('/expenses/pay-settlement', {
        groupId,
        fromUserId,
        toUserId,
        amount
      });
      
      alert('Payment completed successfully!');
      fetchReportData();
    } catch (error) {
      console.error('Error processing payment:', error);
      setError('Failed to process payment. Please try again.');
    }
  };

  const handleCustomSplitChange = (userId, percentage) => {
    setCustomSplits(prev => {
      const existingIndex = prev.findIndex(split => split.user === userId);
      
      if (existingIndex >= 0) {
        const updated = [...prev];
        updated[existingIndex] = { ...updated[existingIndex], percentage: parseFloat(percentage) || 0 };
        return updated;
      } else {
        return [...prev, { user: userId, percentage: parseFloat(percentage) || 0 }];
      }
    });
  };

  const calculateCustomSplitAmounts = () => {
    if (!report || !report.members || customSplits.length === 0) return {};
    
    const totalAmount = parseFloat(newExpense.amount) || 0;
    const amounts = {};
    
    customSplits.forEach(split => {
      amounts[split.user] = (totalAmount * split.percentage) / 100;
    });
    
    return amounts;
  };

  // Safe comparison function for IDs
  const compareIds = (id1, id2) => {
    if (!id1 || !id2) return false;
    return id1.toString() === id2.toString();
  };

  // Check if current user is the one who needs to pay in a settlement
  const isCurrentUserDebtor = (settlement) => {
    return compareIds(settlement.from?._id, user.id);
  };

  // Check if current user is the one who needs to pay in an expense split
  const shouldShowMarkPaid = (expense, split) => {
    return (
      !split.paid && 
      split.user && 
      compareIds(split.user._id, user.id) &&
      expense.paidBy && 
      !compareIds(expense.paidBy._id, user.id)
    );
  };

  // Check if current user is group creator
  const isGroupCreator = () => {
    return report && report.createdBy && compareIds(report.createdBy._id, user.id);
  };

  const addMemberEmail = (email) => {
    if (!newMemberEmails.includes(email)) {
      setNewMemberEmails([...newMemberEmails, email]);
    }
    setSearchQuery('');
    setSearchResults([]);
  };

  const removeMemberEmail = (email) => {
    setNewMemberEmails(newMemberEmails.filter(e => e !== email));
  };

  const resetExpenseForm = () => {
    setNewExpense({ description: '', amount: '', splitType: 'equal' });
    setCustomSplits([]);
    setShowAddExpense(false);
    setShowCustomSplitModal(false);
  };

  const resetMemberForm = () => {
    setShowAddMemberModal(false);
    setNewMemberEmails([]);
    setSearchQuery('');
    setSearchResults([]);
  };

  const openCustomSplitModal = () => {
    if (!report || !report.members) return;
    
    // Initialize custom splits with equal percentages
    const equalPercentage = (100 / report.members.length).toFixed(2);
    const initialSplits = report.members.map(member => ({
      user: member._id,
      percentage: parseFloat(equalPercentage)
    }));
    
    setCustomSplits(initialSplits);
    setShowCustomSplitModal(true);
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="text-center">Loading report...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        
        {/* Error Message */}
        {error && (
          <div className={`mb-6 ${error.includes('successfully') ? 'bg-green-100 border-green-400 text-green-700' : 'bg-red-100 border-red-400 text-red-700'} px-4 py-3 rounded border`}>
            <strong>{error.includes('successfully') ? 'Success:' : 'Error:'}</strong> {error}
          </div>
        )}

        <div className="flex justify-between items-center mb-6">
          <div>
            <h1 className="text-2xl font-bold text-gray-900">Expense Report</h1>
            {report && report.members && (
              <p className="text-sm text-gray-500 mt-1">
                {report.members.length} members in this group
                {isGroupCreator() && ' • You are the group creator'}
              </p>
            )}
          </div>
          <div className="flex space-x-2">
            {isGroupCreator() && (
              <button
                onClick={() => setShowAddMemberModal(true)}
                className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-md text-sm font-medium"
              >
                Add Members
              </button>
            )}
            <button
              onClick={() => setShowAddExpense(true)}
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md text-sm font-medium"
            >
              Add Expense
            </button>
          </div>
        </div>

        {/* Settlement Summary */}
        <div className="bg-white shadow rounded-lg mb-6">
          <div className="px-4 py-5 sm:px-6">
            <h3 className="text-lg leading-6 font-medium text-gray-900">Settlement Summary</h3>
            <p className="mt-1 text-sm text-gray-500">
              Click "Pay" to mark settlements as paid. This will update for all group members.
            </p>
          </div>
          <div className="border-t border-gray-200 px-4 py-5 sm:p-6">
            {report && report.settlements && report.settlements.length > 0 ? (
              <div className="space-y-3">
                {report.settlements.map((settlement, index) => (
                  <div key={index} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                    <div className="flex items-center space-x-3">
                      <span className="text-sm font-medium text-gray-900">
                        {settlement.from?.name || 'Unknown User'}
                        {settlement.from && compareIds(settlement.from._id, user.id) && (
                          <span className="ml-1 text-orange-600 font-medium">(You)</span>
                        )}
                      </span>
                      <span className="text-gray-500">→</span>
                      <span className="text-sm font-medium text-gray-900">
                        {settlement.to?.name || 'Unknown User'}
                        {settlement.to && compareIds(settlement.to._id, user.id) && (
                          <span className="ml-1 text-blue-600 font-medium">(You)</span>
                        )}
                      </span>
                    </div>
                    <div className="flex items-center space-x-3">
                      <span className="text-lg font-bold text-green-600">
                        ₹{settlement.amount?.toFixed(2) || '0.00'}
                      </span>
                      {isCurrentUserDebtor(settlement) && (
                        <button
                          onClick={() => handlePaySettlement(
                            settlement.from?._id, 
                            settlement.to?._id, 
                            settlement.amount
                          )}
                          className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded text-sm font-medium"
                        >
                          Pay
                        </button>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-gray-500 text-center">All expenses are settled! 🎉</p>
            )}
          </div>
        </div>

        {/* Expenses List */}
        <div className="bg-white shadow rounded-lg">
          <div className="px-4 py-5 sm:px-6">
            <h3 className="text-lg leading-6 font-medium text-gray-900">All Expenses</h3>
            <p className="mt-1 text-sm text-gray-500">
              Click "Mark Paid" to update payment status for individual expense splits.
            </p>
          </div>
          <div className="border-t border-gray-200">
            <ul className="divide-y divide-gray-200">
              {expenses.map((expense) => (
                <li key={expense._id} className="px-4 py-4">
                  <div className="flex items-center justify-between">
                    <div className="flex-1">
                      <h4 className="text-sm font-medium text-gray-900">{expense.description}</h4>
                      <p className="text-sm text-gray-500">
                        Paid by {expense.paidBy?.name || 'Unknown User'} • ₹{expense.amount} • {expense.splitType} split
                        {expense.paidBy && compareIds(expense.paidBy._id, user.id) && (
                          <span className="ml-2 text-blue-600 font-medium">(You paid)</span>
                        )}
                      </p>
                      <div className="mt-2 space-y-1">
                        {expense.splits?.map((split, index) => (
                          <div key={index} className="flex items-center justify-between text-sm">
                            <span className={split.paid ? 'text-green-600' : 'text-gray-600'}>
                              {split.user?.name || 'Unknown User'} owes ₹{split.amount?.toFixed(2) || '0.00'}
                              {expense.splitType === 'custom' && split.percentage && (
                                <span className="ml-1 text-purple-600">({split.percentage}%)</span>
                              )}
                              {split.user && compareIds(split.user._id, user.id) && (
                                <span className="ml-1 text-orange-600 font-medium">(You)</span>
                              )}
                            </span>
                            <div className="flex items-center space-x-2">
                              <span className={split.paid ? 'text-green-600' : 'text-red-600'}>
                                {split.paid ? '✅ Paid' : '❌ Not Paid'}
                              </span>
                              {shouldShowMarkPaid(expense, split) && (
                                <button
                                  onClick={() => handleMarkAsPaid(expense._id, split.user._id)}
                                  className="text-blue-600 hover:text-blue-800 text-sm font-medium"
                                >
                                  Mark Paid
                                </button>
                              )}
                            </div>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                </li>
              ))}
              {expenses.length === 0 && (
                <li className="px-4 py-8 text-center text-gray-500">
                  No expenses yet. Add your first expense!
                </li>
              )}
            </ul>
          </div>
        </div>

        {/* Add Expense Modal */}
        {showAddExpense && (
          <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
            <div className="relative top-20 mx-auto p-5 border w-full max-w-md shadow-lg rounded-md bg-white">
              <div className="mt-3">
                <h3 className="text-lg font-medium text-gray-900 mb-4">Add New Expense</h3>
                
                <form onSubmit={handleAddExpense}>
                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Description *
                    </label>
                    <input
                      type="text"
                      required
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                      placeholder="e.g., Dinner, Hotel, Taxi"
                      value={newExpense.description}
                      onChange={(e) => setNewExpense({ ...newExpense, description: e.target.value })}
                      disabled={addExpenseLoading}
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Amount (₹) *
                    </label>
                    <input
                      type="number"
                      step="0.01"
                      min="0.01"
                      required
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                      placeholder="0.00"
                      value={newExpense.amount}
                      onChange={(e) => setNewExpense({ ...newExpense, amount: e.target.value })}
                      disabled={addExpenseLoading}
                    />
                  </div>

                  <div className="mb-6">
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Split Type
                    </label>
                    <select
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                      value={newExpense.splitType}
                      onChange={(e) => setNewExpense({ ...newExpense, splitType: e.target.value })}
                      disabled={addExpenseLoading}
                    >
                      <option value="equal">Equal Split</option>
                      <option value="custom">Custom Split (by percentage)</option>
                    </select>
                    
                    {newExpense.splitType === 'custom' && report && report.members && (
                      <div className="mt-2">
                        <button
                          type="button"
                          onClick={openCustomSplitModal}
                          className="bg-purple-600 hover:bg-purple-700 text-white px-3 py-1 rounded text-sm"
                        >
                          Set Custom Percentages
                        </button>
                        {customSplits.length > 0 && (
                          <p className="text-xs text-gray-500 mt-1">
                            Custom splits configured for {customSplits.length} members
                          </p>
                        )}
                      </div>
                    )}
                  </div>

                  <div className="flex justify-end space-x-3">
                    <button
                      type="button"
                      onClick={resetExpenseForm}
                      disabled={addExpenseLoading}
                      className="px-4 py-2 text-sm font-medium text-gray-700 hover:text-gray-500 disabled:opacity-50"
                    >
                      Cancel
                    </button>
                    <button
                      type="submit"
                      disabled={addExpenseLoading}
                      className="px-4 py-2 text-sm font-medium text-white bg-blue-600 rounded-md hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
                    >
                      {addExpenseLoading ? (
                        <>
                          <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                          </svg>
                          Adding...
                        </>
                      ) : (
                        'Add Expense'
                      )}
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        )}

        {/* Custom Split Modal */}
        {showCustomSplitModal && report && report.members && (
          <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
            <div className="relative top-20 mx-auto p-5 border w-full max-w-md shadow-lg rounded-md bg-white">
              <div className="mt-3">
                <h3 className="text-lg font-medium text-gray-900 mb-4">Set Custom Percentages</h3>
                <p className="text-sm text-gray-500 mb-4">
                  Set percentage for each member. Total must be 100%.
                </p>
                
                <div className="space-y-3 max-h-64 overflow-y-auto">
                  {report.members.map((member) => {
                    const split = customSplits.find(s => compareIds(s.user, member._id));
                    const percentage = split?.percentage || 0;
                    const amount = ((parseFloat(newExpense.amount) || 0) * percentage) / 100;
                    
                    return (
                      <div key={member._id} className="flex items-center justify-between p-2 border rounded">
                        <span className="text-sm font-medium">{member.name}</span>
                        <div className="flex items-center space-x-2">
                          <input
                            type="number"
                            step="0.01"
                            min="0"
                            max="100"
                            className="w-20 px-2 py-1 border rounded text-sm"
                            value={percentage}
                            onChange={(e) => handleCustomSplitChange(member._id, e.target.value)}
                            placeholder="%"
                          />
                          <span className="text-xs text-gray-500 w-16">
                            ₹{amount.toFixed(2)}
                          </span>
                        </div>
                      </div>
                    );
                  })}
                </div>
                
                <div className="mt-4 p-2 bg-gray-50 rounded">
                  <div className="flex justify-between text-sm">
                    <span>Total Percentage:</span>
                    <span className="font-medium">
                      {customSplits.reduce((sum, split) => sum + (split.percentage || 0), 0).toFixed(2)}%
                    </span>
                  </div>
                  <div className="flex justify-between text-sm mt-1">
                    <span>Total Amount:</span>
                    <span className="font-medium">
                      ₹{((parseFloat(newExpense.amount) || 0) * customSplits.reduce((sum, split) => sum + (split.percentage || 0), 0) / 100).toFixed(2)}
                    </span>
                  </div>
                </div>

                <div className="flex justify-end space-x-3 mt-4">
                  <button
                    type="button"
                    onClick={() => setShowCustomSplitModal(false)}
                    className="px-4 py-2 text-sm font-medium text-gray-700 hover:text-gray-500"
                  >
                    Cancel
                  </button>
                  <button
                    type="button"
                    onClick={() => setShowCustomSplitModal(false)}
                    className="px-4 py-2 text-sm font-medium text-white bg-purple-600 rounded-md hover:bg-purple-700"
                  >
                    Save Percentages
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Add Members Modal */}
        {showAddMemberModal && (
          <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
            <div className="relative top-20 mx-auto p-5 border w-full max-w-md shadow-lg rounded-md bg-white">
              <div className="mt-3">
                <h3 className="text-lg font-medium text-gray-900 mb-4">Add Members to Group</h3>
                
                <form onSubmit={handleAddMembers}>
                  <div className="mb-4">
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Invite Members by Email
                    </label>
                    <input
                      type="text"
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                      placeholder="Search by email..."
                      value={searchQuery}
                      onChange={(e) => {
                        setSearchQuery(e.target.value);
                        handleSearchUsers(e.target.value);
                      }}
                      disabled={addMemberLoading}
                    />
                    
                    {searchResults.length > 0 && (
                      <div className="mt-1 border border-gray-300 rounded-md bg-white max-h-32 overflow-y-auto">
                        {searchResults.map((user) => (
                          <div
                            key={user._id}
                            className="px-3 py-2 hover:bg-gray-100 cursor-pointer text-sm"
                            onClick={() => addMemberEmail(user.email)}
                          >
                            {user.name} ({user.email})
                          </div>
                        ))}
                      </div>
                    )}

                    {newMemberEmails.length > 0 && (
                      <div className="mt-2 flex flex-wrap gap-2">
                        {newMemberEmails.map((email) => (
                          <span
                            key={email}
                            className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800"
                          >
                            {email}
                            <button
                              type="button"
                              className="ml-1 hover:bg-green-200 rounded-full"
                              onClick={() => removeMemberEmail(email)}
                              disabled={addMemberLoading}
                            >
                              ×
                            </button>
                          </span>
                        ))}
                      </div>
                    )}
                  </div>

                  <div className="flex justify-end space-x-3">
                    <button
                      type="button"
                      onClick={resetMemberForm}
                      disabled={addMemberLoading}
                      className="px-4 py-2 text-sm font-medium text-gray-700 hover:text-gray-500 disabled:opacity-50"
                    >
                      Cancel
                    </button>
                    <button
                      type="submit"
                      disabled={addMemberLoading || newMemberEmails.length === 0}
                      className="px-4 py-2 text-sm font-medium text-white bg-green-600 rounded-md hover:bg-green-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
                    >
                      {addMemberLoading ? (
                        <>
                          <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                          </svg>
                          Adding...
                        </>
                      ) : (
                        'Add Members'
                      )}
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ReportsPage;
